(window.webpackJsonp=window.webpackJsonp||[]).push([[13],{434:function(v,t,_){"use strict";_.r(t);var a=_(65),r=Object(a.a)({},(function(){var v=this,t=v.$createElement,_=v._self._c||t;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"自动驾驶技术简析"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#自动驾驶技术简析"}},[v._v("#")]),v._v(" 自动驾驶技术简析")]),v._v(" "),_("p",[v._v("自动驾驶系统是在普通汽车的基础上增加摄像机、激光雷达、毫米波雷达、超声波雷达等先进的传感器以及控制器、执行器等装置，通过车载传感系统和信息终端实现车与X（人、车、路、云等）智能信息交换，具备智能的环境感知能力，能够自动地分析汽车行驶的安全及危险状态，按照人的意志到达目的地，最终实现自动操作行驶。")]),v._v(" "),_("p",[v._v("智能汽车利用车载传感器进行综合感知，具有自主决策能力，能够控制车辆安全，高效地行驶，是实现未来交通的重要发展途径。从功能上划分，典型的智能驾驶主要包括感知、决策与规划、控制等三个模块，每个模块分为不同的小模块（图1所示）。通过各模块之间的协调配合，实现特定条件下的智能驾驶，其中，综合感知层将传感器系统采集的数据进行处理与融合，实现车辆对环境、位置、交通、目标与障碍物、车辆状态等信息的精确感知；决策规划层根据综合感知和车辆状态信息，结合车辆与环境实际的空间和时间约束，输出车辆下一阶段驾驶行为与运动轨迹，是连接综合感知层与控制执行层的关键模块；控制执行层是依据轨迹规划模块输出的结果，对车辆期望动作进行实现并获取状态反馈信息。决策规划层和控制执行层均是以车辆模型为基础展开研究，且其结果将直接决定车辆目标动作与实现效果，因此对行驶安全性与经济性起到关键影响。")]),v._v(" "),_("h2",{attrs:{id:"一、自动驾驶技术路线"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#一、自动驾驶技术路线"}},[v._v("#")]),v._v(" 一、自动驾驶技术路线")]),v._v(" "),_("p",[v._v("张亚勤院士领军清华大学智能产业研究院（AIR）和百度Apollo联合发布《面向自动驾驶的车路协同关键技术与展望》中明确，自动驾驶有单车智能自动驾驶（Autonomous Driving,AD）和车路协同自动驾驶（Vehicle-Infrastructure Cooperated Autonomous Driving,VICAD）两种技术路线。其中AD主要依靠车辆自身的视觉、毫米波雷达、激光雷达等传感器、计算单元、线控系统进行环境感知、计算决策和控制执行。VICAD则是在单车智能自动驾驶的基础上，通过车联网将“人-车-路-云”交通参与要素有机地联系在一起，助力自动驾驶车辆在环境感知、计算决策和控制执行等方面的能力升级，加速自动驾驶应用成熟。")]),v._v(" "),_("h3",{attrs:{id:"一-单车智能自动驾驶"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#一-单车智能自动驾驶"}},[v._v("#")]),v._v(" （一）单车智能自动驾驶")]),v._v(" "),_("p",[v._v("单车智能自动驾驶的环境感知是通过车上安装的传感器完成对周围环境的探测和定位功能。计算决策一方面将传感器数据进行分析处理，实现对目标的识别；另一方面进行行为预测和全局路径规划、局部路径规划和即时动作规划，决定车辆当前及未来的运行轨迹。控制执行主要包括车辆的运动控制以及人机交互，决定每个执行器如电机、油门、刹车等控制信号。")]),v._v(" "),_("p",[v._v("单车智能高等级自动驾驶规模商业化落地仍然面临着诸多挑战：")]),v._v(" "),_("ol",[_("li",[v._v("自动驾驶安全依然面临着巨大挑战")])]),v._v(" "),_("p",[v._v("高等级自动驾驶车辆目前面临的安全问题主要包括：软硬件系统出现错误或漏洞；感知容易受到遮挡、恶劣天气等环境挑战影响出现失效；目标运动行为出现预测能力不足、决策时间超时和生成轨迹错误的现象；车辆难以高效准确获取道路设施提供的交通规则、交通状态等信息。")]),v._v(" "),_("ol",{attrs:{start:"2"}},[_("li",[v._v("单车感知长尾问题限制了车辆可运行设计域（ODD:Operational Design Domain)")])]),v._v(" "),_("p",[v._v("感知的长尾问题是当前限制单车智能自动驾驶车辆ODD的主要原因之一。受车端传感器安装位置、探测距离、视场角、数据吞吐、标定精度、时间同步等限制，车辆在繁忙路口、恶劣天气、小物体感知识别、信号灯识别、逆光等环境条件中行驶时，仍然难以彻底解决准确感知识别和高精度定位问题。")]),v._v(" "),_("ol",{attrs:{start:"3"}},[_("li",[v._v("自动驾驶的经济性问题还未得到充分解决")])]),v._v(" "),_("p",[v._v("为了实现高等级自动驾驶，车载传感器的数量需要显著增加，硬件成本过高，难以保证车辆的经济性。另外，为了确保自动驾驶安全，会在车端部署冗余传感器系统、高精度地图、及相应的软件系统，也大大增加了自动驾驶车辆的成本。")]),v._v(" "),_("h3",{attrs:{id:"二-车路协同自动驾驶"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#二-车路协同自动驾驶"}},[v._v("#")]),v._v(" （二）车路协同自动驾驶")]),v._v(" "),_("p",[_("img",{attrs:{src:"https://picture-1305384466.cos.ap-nanjing.myqcloud.com/%E8%87%AA%E5%8A%A8%E9%A9%BE%E9%A9%B6%E6%8A%80%E6%9C%AF%E8%A7%A3%E6%9E%90%E5%9B%BE%E7%89%871.png",alt:""}})]),v._v(" "),_("p",[v._v("车路协同自动驾驶（VehicleInfrastructure Cooperated Autonomous Driving,VICAD）是采用先进的无线通信和新一代互联网等技术，全方位实施车与车、车与路、车与人之间动态实时信息交互，并在全时空动态交通信息采集与融合的基础上开展车辆主动安全控制和道路协同管理，充分实现人车路的有效协同，保证交通安全，提高通行效率，从而形成安全、高效和环保的道路交通系统。")]),v._v(" "),_("ol",[_("li",[_("p",[v._v("车路协同自动驾驶是在单车智能自动驾驶的基础上，通过先进的车、道路感知和定位设备（如摄像头、雷达等）对道路交通环境进行实时高精度感知定位，按照约定协议进行数据交互，实现车与车、车与路、车与人之间不同程度的信息交互共享（网络互联化），并涵盖不同程度的车辆自动化驾驶阶段（车辆自动化），以及考虑车辆与道路之间协同优化问题（系统集成化）。通过车辆自动化、网络互联化和系统集成化，最终构建一个车路协同自动驾驶系统。")])]),v._v(" "),_("li",[_("p",[v._v("车路协同自动驾驶由智能车辆、智能道路两大关键部分组成。智能车辆：可以是不同网联等级和自动化程度的车辆；智能道路：可包括以下设备设施：道路工程及配套附属设施；智能感知设施（摄像头、毫米波雷达、激光雷达等）；路侧通信设施（直连无线通信设施、蜂窝移动通信设施）；计算控制设施（边缘计算节点、MEC或各级云平台）；高精度地图与辅助定位设施；电力功能等配套附属设备等。Apollo智能路测感知系统（图2所示）。")])]),v._v(" "),_("li",[_("p",[v._v("车路协同自动驾驶的关键技术包括：协同感知技术（传感器高精度标定技术；环境感知技术；融合与预测技术）；高精度地图与高精度定位技术（高精度地图；路侧辅助定位技术）；协同决策与协同控制技术（意图预测、博弈仲裁、引导调度等协同决策；车辆、设施、人类等协同控制引导）；高可靠低时延网络通信技术（直连无线通信技术；蜂窝移动通信技术；交通系统集成优化与有线网络传输技术等）；云计算技术（边缘计算MEC技术；多级云控平台技术；大数据和人工智能平台技术）；功能安全与预期功能安全；物联网Io T技术；网络安全技术等。")])]),v._v(" "),_("li",[_("p",[v._v("车路协同自动驾驶的技术优势体现在以下三个方面")]),v._v(" "),_("p",[v._v("(1)环境感知")])])]),v._v(" "),_("p",[v._v("车路协同自动驾驶通过车路协同、车车协同，能够极大地拓展单车的感知范围，并且不受遮挡限制，能够让单车提早发现未知状况，能够应对目标突然驶入等目前在自动驾驶测试和事故中难以应对的状况。此外，单车智能自动驾驶在目标预测、驾驶意图“博弈”等方面存在困难。车路协同能够直接给出关键结果状态信息，例如信号灯状态、周边车辆的下一步动作意图、当前路况下最佳的行驶路线等，减少了复杂的基于传感信息的计算处理过程，并且能够准确地了解周围交通参与者的意图。")]),v._v(" "),_("p",[v._v("(2)计算决策")]),v._v(" "),_("p",[v._v("车路协同系统有望分担单车的算力消耗，基于云控平台给出全局最优的驾驶策略。在算力方面，一是车路协同作为“超级传感器”能够直接给出感知的目标结果，省去了复杂的对传感器信号的计算分析过程，如红绿灯的判断，从而大大减轻了单车的算力需求；二是能够借助云计算、边缘计算等能力，有望将路侧的算力引入，例如在路侧安装视觉传感器、激光雷达等传感器，将路侧感知结果进行下发等。在驾驶策略方面，在特定场景下，车路协同能够集中采集其范围内的交通参与主体，根据所有主体的目的和状态，给出全局最优的解决方案，无需再通过“试探”和“博弈”给出决策规划，在矿山、港口、物流等非公共开放道路的特定场景下已经得到验证应用。")]),v._v(" "),_("p",[v._v("(3)控制执行")]),v._v(" "),_("p",[v._v("车路协同在控制执行方面能够提供远程遥控驾驶、协同驾驶的应用模式。例如在某些危险或不适合人类进入的场合，需要通过5G远程遥控驾驶来操作远端的车辆进行作业，目前在无人矿山等场合下已得到应用。在车辆编队行驶等方面，借助头车和后排车辆的控制执行信息交互，后排车辆能够按照头车的统一命令进行驾驶，减轻后排车辆的感知计算任务负载。")]),v._v(" "),_("h2",{attrs:{id:"自动驾驶技术等级"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#自动驾驶技术等级"}},[v._v("#")]),v._v(" 自动驾驶技术等级")]),v._v(" "),_("p",[v._v("车辆可运行设计域ODD（或自动驾驶系统功能设定的运行条件）是驾驶自动化系统设计时确定的适用于其功能运行的外部环境条件。")]),v._v(" "),_("p",[v._v("SAE J2980标准中推荐使用六大要素构建运行设计域，包括位置（公路、乡村道路、城市道路、交叉口、土路、越野、停车场、车道、维修车库），道路状况（道路摩擦（干、冰、雪、湿、裂）、坡度、道路宽度），驾驶操作（启动、停车、向前行驶、向后行驶、直行、转弯、避开车道、变道、关闭），车辆状态（滑行、爬行、加速、减速、驻车、停止、碰撞），其他考虑因素（侧风，即将来临的交通，施工区域，事故场景，交通堵塞，行人），其他车辆特征（其他车辆系统的状态：故障代码、拖车、重载、启动关闭/打开、远程车辆启动、其他技术）。")]),v._v(" "),_("p",[v._v("2022年3月1日起实施《汽车驾驶自动化分级》国家标准（GB/T40429-2021），将驾驶自动化等级划分成0～5级。")]),v._v(" "),_("h3",{attrs:{id:"一-0级驾驶自动化-应急辅助"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#一-0级驾驶自动化-应急辅助"}},[v._v("#")]),v._v(" （一）0级驾驶自动化（应急辅助）")]),v._v(" "),_("p",[v._v("驾驶自动化系统不能持续执行动态驾驶任务中的车辆横向或纵向运动控制，但具备持续执行动态驾驶任务中的部分目标和事件探测与响应的能力。")]),v._v(" "),_("p",[v._v("0级驾驶自动化不是无驾驶自动化，0级驾驶自动化可感知环境，并提供报警、辅助或短暂介入以辅助驾驶员（如车道偏离预警、前碰撞预警、自动紧急制动等应急辅助功能）。")]),v._v(" "),_("p",[v._v("不具备目标和事件探测与响应的能力的功能（如：定速巡航、电子稳定性控制等）不在驾驶自动化考虑的范围内。")]),v._v(" "),_("h3",{attrs:{id:"二-1级驾驶自动化-部分驾驶辅助"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#二-1级驾驶自动化-部分驾驶辅助"}},[v._v("#")]),v._v(" （二）1级驾驶自动化（部分驾驶辅助）")]),v._v(" "),_("p",[v._v("驾驶自动化系统在其设计运行条件ODD内持续地执行动态驾驶任务中的车辆横向或纵向运动控制，且具备与所执行的车辆横向或纵向运动控制相适应的部分目标和事件探测与响应的能力。")]),v._v(" "),_("p",[v._v("对于1级驾驶自动化，驾驶员和驾驶自动化系统共同执行动态驾驶任务，并监管驾驶自动化系统的行为和执行适当的响应或操作。")]),v._v(" "),_("p",[_("img",{attrs:{src:"https://picture-1305384466.cos.ap-nanjing.myqcloud.com/%E8%87%AA%E5%8A%A8%E9%A9%BE%E9%A9%B6%E6%8A%80%E6%9C%AF%E8%A7%A3%E6%9E%90%E5%9B%BE%E7%89%872.png",alt:""}})]),v._v(" "),_("h3",{attrs:{id:"三-2级驾驶自动化-组合驾驶辅助"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#三-2级驾驶自动化-组合驾驶辅助"}},[v._v("#")]),v._v(" （三）2级驾驶自动化（组合驾驶辅助）")]),v._v(" "),_("p",[v._v("驾驶自动化系统在其设计运行条件内持续地执行动态驾驶任务中的车辆横向和纵向运动控制，且具备与所执行的车辆横向和纵向运动控制相适应的部分目标和事件探测与响应的能力。")]),v._v(" "),_("p",[v._v("对于2级驾驶自动化，驾驶员和驾驶自动化系统共同执行动态驾驶任务，并监管驾驶自动化系统的行为和执行适当的响应或操作。")]),v._v(" "),_("h3",{attrs:{id:"四-3级驾驶自动化-有条件自动驾驶"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#四-3级驾驶自动化-有条件自动驾驶"}},[v._v("#")]),v._v(" （四）3级驾驶自动化（有条件自动驾驶）")]),v._v(" "),_("p",[v._v("驾驶自动化系统在其设计运行条件内持续地执行全部动态驾驶任务。")]),v._v(" "),_("p",[v._v("对于3级驾驶自动化，动态驾驶任务接管用户以适当的方式执行动态驾驶任务接管。")]),v._v(" "),_("h3",{attrs:{id:"五-4级驾驶自动化-高度自动驾驶"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#五-4级驾驶自动化-高度自动驾驶"}},[v._v("#")]),v._v(" （五）4级驾驶自动化（高度自动驾驶）")]),v._v(" "),_("p",[v._v("驾驶自动化系统在其设计运行条件内持续地执行全部动态驾驶任务和执行动态驾驶任务接管。")]),v._v(" "),_("p",[v._v("对于4级驾驶自动化，系统发出接管请求时，若乘客无响应，系统具备自动达到最小风险状态的能力。")]),v._v(" "),_("h3",{attrs:{id:"六-5级驾驶自动化-完全自动驾驶"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#六-5级驾驶自动化-完全自动驾驶"}},[v._v("#")]),v._v(" （六）5级驾驶自动化（完全自动驾驶）")]),v._v(" "),_("p",[v._v("驾驶自动化系统在任何可行驶条件下持续地执行全部动态驾驶任务和执行动态驾驶任务接管。")]),v._v(" "),_("p",[v._v("对于5级驾驶自动化，系统发出接管请求时，乘客无需进行响应，系统具备自动达到最小风险状态的能力。5级驾驶自动化在车辆可行驶环境下没有设计运行条件的限制（商业和法规因素等限制除外）。")]),v._v(" "),_("p",[v._v("SAE（美国汽车工程师学会）亦将自动驾驶分为6个等级，其中L0：应急辅助，L1：部分驾驶辅助，L2：组合驾驶辅助，L3：有条件自动驾驶，L4：高度自动驾驶，L5：完全自动驾驶。基本对应中国标准《汽车驾驶自动化分级》自动驾驶驾驶自动化等级0～5级。SAE将L0-L2级系统命名为“驾驶员辅助系统”，而L3级至L5级则被视为“自动驾驶系统”。驾驶员辅助系统仅限于提供警告和瞬间帮助；提供车道居中或自适应巡航控制；或同时提供车道居中和自适应巡航控制。驾驶员辅助功能要求驾驶员不断监督，并根据需要进行转向、制动或加速。另一方面，L3级和L4级自动驾驶系统可以在有限的条件下驾驶车辆，除非满足所有要求的条件，否则不会运行自动驾驶。L5级自动驾驶系统可以在所有条件下驾驶车辆。对于L3级，当功能要求时，必须由人类来驾驶。对于4级和5级，自动驾驶功能将不要求人类接管。")]),v._v(" "),_("h2",{attrs:{id:"参考文献"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#参考文献"}},[v._v("#")]),v._v(" 参考文献")]),v._v(" "),_("p",[v._v("[1] 面向自动驾驶的车路协同关键技术与展望，清华大学智能产业研究院(AIR)、百度Apollo,2021[2]汽车智能网联技术概论，刘志忠杨平，清华大学出版社，2021年5月")]),v._v(" "),_("p",[v._v("[3] GB/T 40429-2021《汽车驾驶自动化分级》，国家市场监督管理总局、国家标准化管理委员会")])])}),[],!1,null,null,null);t.default=r.exports}}]);